package com.smartling.api.sdk.auth;

import com.smartling.api.sdk.HttpClientConfiguration;
import com.smartling.api.sdk.ProxyConfiguration;
import com.smartling.api.sdk.dto.file.StringResponse;
import com.smartling.api.sdk.exceptions.SmartlingApiException;
import com.smartling.api.sdk.file.response.Response;
import com.smartling.api.sdk.util.HttpUtils;
import com.smartling.web.api.v2.ResponseCode;
import org.apache.http.client.methods.HttpPost;
import org.apache.http.client.methods.HttpRequestBase;
import org.junit.Before;
import org.junit.Test;
import org.mockito.ArgumentCaptor;

import static mockit.Deencapsulation.setField;
import static org.junit.Assert.assertEquals;
import static org.mockito.Matchers.eq;
import static org.mockito.Matchers.isNull;
import static org.mockito.Mockito.mock;
import static org.mockito.Mockito.when;

public class AuthApiClientTest
{
    private HttpUtils httpUtils;
    private ArgumentCaptor<HttpRequestBase> requestCaptor = ArgumentCaptor.forClass(HttpRequestBase.class);
    private ProxyConfiguration proxyConfiguration;
    private StringResponse response;
    private AuthApiClient authApiClient;

    private static final String BASE_PATH = "https://api.smartling.com";
    private static final String USER_ID = "USER_ID";
    private static final String USER_SECRET = "USER_SECRET";

    private static final String AUTH_RESPONSE = "{\n"
            + "  \"response\": {\n"
            + "    \"code\": \"SUCCESS\",\n"
            + "    \"data\": {\n"
            + "      \"accessToken\": \"eyJhbGciOiJSUzI1NiJ9"
            + "..K0HT0wOqQZzWEVWWeo1Uy21BZvbBj2rg0Kbvvz1ZhQA1l4pgx1KdkX1UCUiaIAdlSCv7N-O8EJwTSpVtwH-xHjAVwZavhRnbCMRqJOF1gB98NxUUA7Qp5w4Vt22hFZ6kfM72UAmfPs46VT8L61oqoRKUe9oxTF0WOOFDr9zpWbs\",\n"
            + "      \"refreshToken\": \"eyJhbGciOiJSUzI1NiJ9"
            + "..Bjwzew-pwFB_8sG20OezmoCWMMqzZNjpQvMtd_yrNLJey9c3hqAGuXRM5yjtbKcHM5xkzvxOw4UBUIbVq6ElWVGsvrYXIYzEpCv7L_X2I9-cTJ9gC4kw8rWqXGWx2JYEh0WYsBs623lCB_2xjthP21sX94iwlOJVX3_3Huxw_48\",\n"
            + "      \"expiresIn\": 300,\n"
            + "      \"refreshExpiresIn\": 3660,\n"
            + "      \"tokenType\": \"Bearer\"\n"
            + "    }\n"
            + "  }\n"
            + "}";

    @Before
    public void setup() throws SmartlingApiException
    {
        httpUtils = mock(HttpUtils.class);
        response = mock(StringResponse.class);
        proxyConfiguration = mock(ProxyConfiguration.class);
        authApiClient = new AuthApiClient(proxyConfiguration, "https://api.smartling.com");
        setField(authApiClient, "httpUtils", httpUtils);
        when(httpUtils.executeHttpCall(requestCaptor.capture(), eq(proxyConfiguration), isNull(HttpClientConfiguration.class))).thenReturn(response);
    }

    @Test
    public void testAuthenticate() throws Exception
    {
        when(response.getContents()).thenReturn(AUTH_RESPONSE);
        Response<AuthenticationContext> response = authApiClient.authenticate(USER_ID, USER_SECRET);
        HttpRequestBase request = requestCaptor.getValue();
        assertEquals(HttpPost.class, request.getClass());
        assertEquals("https://api.smartling.com/auth-api/v2/authenticate", request.getURI().toString());
        assertEquals(ResponseCode.SUCCESS, response.getCode());
        assertEquals("eyJhbGciOiJSUzI1NiJ9..K0HT0wOqQZzWEVWWeo1Uy21BZvbBj2rg0Kbvvz1ZhQA1l4pgx1KdkX1UCUiaIAdlSCv7N-O8EJwTSpVtwH-xHjAVwZavhRnbCMRqJOF1gB98NxUUA7Qp5w4Vt22hFZ6kfM72UAmfPs46VT8L61oqoRKUe9oxTF0WOOFDr9zpWbs", response.retrieveData().getAccessToken());
        assertEquals(300, response.retrieveData().getExpiresIn());
    }

    @Test
    public void testRefresh() throws Exception
    {
        when(response.getContents()).thenReturn(AUTH_RESPONSE);
        Response<AuthenticationContext> response = authApiClient.refresh("refreshToken");
        HttpRequestBase request = requestCaptor.getValue();
        assertEquals(HttpPost.class, request.getClass());
        assertEquals("https://api.smartling.com/auth-api/v2/authenticate/refresh", request.getURI().toString());
        assertEquals(ResponseCode.SUCCESS, response.getCode());
        assertEquals("eyJhbGciOiJSUzI1NiJ9..K0HT0wOqQZzWEVWWeo1Uy21BZvbBj2rg0Kbvvz1ZhQA1l4pgx1KdkX1UCUiaIAdlSCv7N-O8EJwTSpVtwH-xHjAVwZavhRnbCMRqJOF1gB98NxUUA7Qp5w4Vt22hFZ6kfM72UAmfPs46VT8L61oqoRKUe9oxTF0WOOFDr9zpWbs", response.retrieveData().getAccessToken());
        assertEquals(300, response.retrieveData().getExpiresIn());
    }
}